import React, { useState } from "react";
import "./App.css";
import LoginForm from "./components/LoginForm";
import RegisterForm from "./components/RegisterForm";
import Dashboard from "./components/Dashboard";
import CreateCV from "./components/CreateCV";

function App() {
  // ✅ Déclaration des états nécessaires
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showCreateCV, setShowCreateCV] = useState(false);

  // ✅ Quand l’utilisateur se connecte avec succès
  const handleLogin = () => {
    setShowLogin(false);
    setIsLoggedIn(true);
  };

  // ✅ Si l’utilisateur ouvre "Créer un CV" sans être connecté
  if (showCreateCV) {
    return <CreateCV onBack={() => setShowCreateCV(false)} />;
  }

  // ✅ Si connecté → affiche Dashboard
  if (isLoggedIn) {
    return <Dashboard onLogout={() => setIsLoggedIn(false)} />;
  }

  // ✅ Page d’accueil principale
  return (
    <div className="app">
      <header>
        <button
          className="connexion-btn"
          onClick={() => {
            setShowLogin(true);
            setShowRegister(false);
          }}
        >
          Connexion
        </button>
      </header>

      {!showLogin && !showRegister && (
        <main>
          <h1>Bienvenue sur le Créateur de CV</h1>
          <p>Générez votre CV facilement ou choisissez un modèle existant.</p>

          <button
            className="create-btn"
            onClick={() => setShowCreateCV(true)} // ✅ ouvre la page CreateCV
          >
            Créer un CV
          </button>

          <h2>Choisissez un modèle</h2>
            <div className="cv-carousel">
              {[
                { id: 1, name: "Modèle Moderne", color: "#5E81F4" },
                { id: 2, name: "Modèle Élégant", color: "#00B894" },
                { id: 3, name: "Modèle Classique", color: "#FDCB6E" },
                { id: 4, name: "Modèle Minimaliste", color: "#E84393" },
                { id: 5, name: "Modèle Professionnel", color: "#0984E3" },
                { id: 6, name: "Modèle Étudiant", color: "#6C5CE7" },
                { id: 7, name: "Modèle Créatif", color: "#D63031" },
              ].map((model) => (
                <div
                  key={model.id}
                  className="cv-model"
                  style={{ borderTop: `5px solid ${model.color}` }}
                >
                  <div className="cv-model-content">
                    <div
                      className="cv-sample"
                      style={{
                        backgroundColor: model.color + "20",
                        border: `1px solid ${model.color}`,
                      }}
                    ></div>
                    <h4>{model.name}</h4>
                    <button className="use-model-btn">Utiliser</button>
                  </div>
                </div>
              ))}
            </div>
        </main>
      )}

      {showLogin && (
        <LoginForm
          onClose={() => setShowLogin(false)}
          onSwitch={() => {
            setShowLogin(false);
            setShowRegister(true);
          }}
          onLogin={handleLogin}
        />
      )}

      {showRegister && (
        <RegisterForm
          onClose={() => setShowRegister(false)}
          onSwitch={() => {
            setShowRegister(false);
            setShowLogin(true);
          }}
        />
      )}
    </div>
  );
}

export default App;
