import React, { useState } from "react";
import { Link } from "react-router-dom"; // Pour navigation React Router

export default function Header() {
  const [open, setOpen] = useState(false);

  const nav = [
    { name: "Accueil", path: "/" },
    { name: "Modèles", path: "/templates" },
    { name: "Aide", path: "/help" },
  ];

  return (
    <header className="w-full bg-white/80 backdrop-blur-md border-b border-gray-200 shadow-sm">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Logo + Nav gauche */}
          <div className="flex items-center gap-8">


            {/* Nav links desktop */}
            <nav className="hidden md:flex gap-6">
              {nav.map((item) => (
                <Link
                  key={item.name}
                  to={item.path}
                  className="text-gray-700 hover:text-gray-900 text-sm font-medium transition-colors duration-150"
                >
                  {item.name}
                </Link>
              ))}
            </nav>
          </div>

          {/* Boutons connexion / inscription à droite */}
          <div className="hidden md:flex items-center gap-3">
            <Link
              to="/login"
              className="text-sm font-medium text-gray-600 hover:text-gray-900"
            >
              Se connecter
            </Link>
            <Link
              to="/register"
              className="ml-2 inline-flex items-center px-4 py-2 rounded-lg bg-indigo-600 text-white text-sm font-medium shadow-sm hover:bg-indigo-700 transition"
            >
              S'inscrire
            </Link>
          </div>



        </div>
      </div>


    </header>
  );
}
