import React, { useEffect, useState } from "react";
import Sidebar from "../../components/sidebar/sidebar";
import Topbar from "../../components/topbar/Topbar";
import "./Dashboard.css";

export default function Dashboard() {
  const [userName, setUserName] = useState("");
  const [cvList, setCvList] = useState([]);

  useEffect(() => {
    const fakeUser = { name: "Lise Fozing" };
    const fakeCVs = [
      { id: 1, title: "CV Développeur Web" },
      { id: 2, title: "CV Data Analyst" }
    ];

    setUserName(fakeUser.name);
    setCvList(fakeCVs);
  }, []);

  return (
    <div className="dashboard-layout">
      {/* Sidebar */}
      <Sidebar />

      {/* Partie droite */}
      <div className="dashboard-content">
        <Topbar />

        {/* Contenu réel du dashboard */}
        <div className="dashboard-inner">

          <h1 className="welcome-title">
            Bienvenue, <span>{userName}</span>
          </h1>

          <button className="create-btn">+ Créer mon CV</button>

          <div className="cv-section">
            <h2>Mes CV</h2>

            {cvList.length === 0 ? (
              <p className="empty-text">Vous n’avez pas encore créé de CV.</p>
            ) : (
              <ul className="cv-list">
                {cvList.map((cv) => (
                  <li key={cv.id} className="cv-item">
                    <span>{cv.title}</span>
                    <button className="cv-view-btn">Voir</button>
                  </li>
                ))}
              </ul>
            )}
          </div>

        </div>
      </div>
    </div>
  );
}
