import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./sidebar.css";

export default function Sidebar({ setSelectedModel }) {
  const [openTemplates, setOpenTemplates] = useState(false);

  // Données des modèles
  const models = [
    { id: "model1", name: "Modèle 1", image: "/images/model1.png" },
    { id: "model2", name: "Modèle 2", image: "/images/model2.png" },
    { id: "model3", name: "Modèle 3", image: "/images/model3.png" },
  ];

  return (
    <aside className="sidebar">
      <div className="sidebar-logo">Dashboard</div>

      <nav className="sidebar-nav">
        <ul>
          <li>
            <Link to="/" className="sidebar-link">
              Accueil
            </Link>
          </li>

          <li>
            <div
              className="sidebar-link dropdown-toggle"
              onClick={() => setOpenTemplates(!openTemplates)}
            >
              Modèles <span className="chevron">{openTemplates ? "▲" : "▼"}</span>
            </div>

            {openTemplates && (
              <ul className="dropdown-menu">
                {models.map((model) => (
                  <li key={model.id} className="model-item">
                    <div className="model-miniature-container">
                      <img
                        src={model.image}
                        alt={model.name}
                        className="model-miniature"
                        onClick={() => setSelectedModel(model.id)}
                      />
                      <span className="model-name">{model.name}</span>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </li>

          <li>
            <Link to="/settings" className="sidebar-link">
              Paramètres
            </Link>
          </li>
        </ul>
      </nav>
    </aside>
  );
}
