import React, { useState } from "react";
import { jsPDF } from "jspdf";
import "./CreateCV.css";

function CreateCV({ onBack }) {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    education: "",
    experience: "",
    skills: "",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("CV enregistré avec succès !");
  };

  const handleGeneratePDF = () => {
    const doc = new jsPDF();

    doc.setFont("helvetica", "normal");
    doc.setFontSize(18);
    doc.text("Curriculum Vitae", 20, 20);
    doc.setFontSize(12);

    doc.text(`Nom : ${formData.name}`, 20, 40);
    doc.text(`Email : ${formData.email}`, 20, 50);
    doc.text(`Téléphone : ${formData.phone}`, 20, 60);

    doc.setFontSize(14);
    doc.text("Formation :", 20, 80);
    doc.setFontSize(12);
    doc.text(formData.education || "-", 30, 90, { maxWidth: 160 });

    doc.setFontSize(14);
    doc.text("Expérience :", 20, 110);
    doc.setFontSize(12);
    doc.text(formData.experience || "-", 30, 120, { maxWidth: 160 });

    doc.setFontSize(14);
    doc.text("Compétences :", 20, 140);
    doc.setFontSize(12);
    doc.text(formData.skills || "-", 30, 150, { maxWidth: 160 });

    doc.save(`${formData.name || "cv"}.pdf`);
  };

  return (
    <div className="create-cv">
      <div className="create-cv-header">
        <button className="back-btn" onClick={onBack}>
          ← Retour
        </button>
        <h2>Créer un CV</h2>
      </div>

      <form className="cv-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nom complet</label>
          <input
            type="text"
            name="name"
            placeholder="Ex: Jean Dupont"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            placeholder="Ex: jean.dupont@gmail.com"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Téléphone</label>
          <input
            type="text"
            name="phone"
            placeholder="Ex: +33 6 12 34 56 78"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Formation</label>
          <textarea
            name="education"
            placeholder="Ex: Diplôme, université, année..."
            value={formData.education}
            onChange={handleChange}
            rows="3"
          ></textarea>
        </div>

        <div className="form-group">
          <label>Expérience</label>
          <textarea
            name="experience"
            placeholder="Ex: Poste, entreprise, durée..."
            value={formData.experience}
            onChange={handleChange}
            rows="3"
          ></textarea>
        </div>

        <div className="form-group">
          <label>Compétences</label>
          <textarea
            name="skills"
            placeholder="Ex: React, Node.js, JavaScript..."
            value={formData.skills}
            onChange={handleChange}
            rows="2"
          ></textarea>
        </div>

        <div className="btn-group">
          <button type="submit" className="save-btn">
            Enregistrer
          </button>
          <button
            type="button"
            className="pdf-btn"
            onClick={handleGeneratePDF}
          >
            Télécharger en PDF
          </button>
        </div>
      </form>
    </div>
  );
}

export default CreateCV;
