import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Topbar.css";

export default function Topbar({ avatar }) {
  const [openMenu, setOpenMenu] = useState(false);
  const [notifications, setNotifications] = useState(0); // <-- initialis√© √† 0

  // Exemple : on peut simuler la r√©cup√©ration depuis le backend
  useEffect(() => {
    // Ici tu peux faire un fetch('/api/notifications') pour r√©cup√©rer les donn√©es
    // Pour l'instant on met une valeur fixe pour test
    setNotifications(0);
  }, []);

  return (
    <header className="topbar">
      {/* Logo CV Maker */}
      <div className="topbar-left">
        <div className="logo-box">CV</div>
        <span className="brand-name">CV Maker</span>
      </div>

      <div className="topbar-right">
        {/* Notifications */}
        <div className="notification-icon">
          üîî
          {notifications > 0 && <span className="badge">{notifications}</span>}
        </div>

        {/* Profil */}
        <div
          className="profile-container"
          onClick={() => setOpenMenu(!openMenu)}
        >
          <img
            src={avatar || "/images/default-avatar.png"}
            alt="Profil"
            className="profile-avatar"
          />
          {openMenu && (
            <ul className="profile-dropdown">
              <li>
                <Link to="/profile">Mon Profil</Link>
              </li>
              <li>
                <Link to="/settings">Param√®tres</Link>
              </li>
              <li>
                <Link to="/help">Aide</Link>
              </li>
              <li>
                <Link to="/">D√©connexion</Link>
              </li>
            </ul>
          )}
        </div>
      </div>
    </header>
  );
}
