import React, { useState } from "react";
import LoginForm from "./components/LoginForm";
import RegisterForm from "./components/RegisterForm";

function App() {
  const [showLogin, setShowLogin] = useState(true);

  const handleLogin = () => alert("Connecté !");
  const handleRegister = (name,email,password) => alert(`Compte créé pour ${name}`);
  const handleClose = () => console.log("Formulaire fermé");

  return (
    <div>
      {showLogin ? (
        <LoginForm
          onClose={handleClose}
          onSwitch={() => setShowLogin(false)}  // ← ici le switch
          onLogin={handleLogin}
        />
      ) : (
        <RegisterForm
          onClose={handleClose}
          onSwitch={() => setShowLogin(true)}   // ← pour revenir au login
          onRegister={handleRegister}
        />
      )}
    </div>
  );
}

export default App;
