import React, { useEffect, useState } from "react";
import Sidebar from "../../components/sidebar/sidebar";
import Topbar from "../../components/topbar/Topbar";
import PreviewSidebar from "../../components/previewsidebar/previewsidebar";
import { FaEye, FaTrash } from "react-icons/fa";
import "./Dashboard.css";

export default function Dashboard() {
  const [userName, setUserName] = useState(""); // ← nom de l'utilisateur connecté
  const [cvList, setCvList] = useState([]); // ← liste des CV de l'utilisateur
  const [selectedModel, setSelectedModel] = useState(null);

  useEffect(() => {
    // Simulation : récupérer les infos depuis le back-end
    const loggedInUser = { name: "Utilisateur" }; // ← valeur générique au départ
    const userCVs = [
      // ← CV existants
      { id: 1, title: "CV Développeur Web" },
      { id: 2, title: "CV Data Analyst" },
    ];

    setUserName(loggedInUser.name);
    setCvList(userCVs);
  }, []);

  const handleDelete = (id) => {
    setCvList(cvList.filter(cv => cv.id !== id));
  };

  return (
    <div className="dashboard-layout">
      <Sidebar setSelectedModel={setSelectedModel} />

      <div className="dashboard-content">
        <Topbar />

        <div className="dashboard-inner">
          <h1 className="welcome-title">
            Bienvenue sur votre espace personnel, <span>{userName}</span>
          </h1>

          <button className="create-btn">+ Créer mon CV</button>

          <div className="cv-section">
            <h2>Mes CV</h2>

            {cvList.length === 0 ? (
              <p className="empty-text">Vous n’avez pas encore créé de CV.</p>
            ) : (
              <ul className="cv-list">
                {cvList.map((cv) => (
                  <li key={cv.id} className="cv-item">
                    <span>{cv.title}</span>
                    <div className="cv-actions">
                      <button className="icon-btn" title="Voir">
                        <FaEye />
                      </button>
                      <button
                        className="icon-btn delete-btn"
                        title="Supprimer"
                        onClick={() => handleDelete(cv.id)}
                      >
                        <FaTrash />
                      </button>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>

      {selectedModel && (
        <PreviewSidebar
          model={selectedModel}
          onClose={() => setSelectedModel(null)}
        />
      )}
    </div>
  );
}
