import React, { useEffect, useState } from "react";
import "./Dashboard.css"; 


export default function Dashboard() {
  const [userName, setUserName] = useState("");
  const [cvList, setCvList] = useState([]);

  useEffect(() => {
    // Simule l'appel backend (tu vas remplacer par fetch/axios)
    const fakeUser = { name: "Lise Fozing" };
    const fakeCVs = [
      { id: 1, title: "CV Développeur Web" },
      { id: 2, title: "CV Data Analyst" }
    ];

    setUserName(fakeUser.name);
    setCvList(fakeCVs);
  }, []);

  return (
    <div className="dashboard-container">
      {/* Message de bienvenue */}
      <h1 className="welcome-title">
        Bienvenue, <span>{userName}</span>
      </h1>

      {/* Bouton pour créer un CV */}
      <button className="create-btn">
        + Créer mon CV
      </button>

      {/* Liste des CV */}
      <div className="cv-section">
        <h2>Mes CV</h2>

        {cvList.length === 0 ? (
          <p className="empty-text">
            Vous n’avez pas encore créé de CV.
          </p>
        ) : (
          <ul className="cv-list">
            {cvList.map((cv) => (
              <li key={cv.id} className="cv-item">
                <span>{cv.title}</span>
                <button className="cv-view-btn">Voir</button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
