import React, { useState } from "react";
import "./App.css";
import LoginForm from "./components/LoginForm";
import RegisterForm from "./components/RegisterForm";
import Dashboard from "./components/Dashboard";

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setShowLogin(false);
    setIsLoggedIn(true);
  };

  if (isLoggedIn) {
    return <Dashboard onLogout={() => setIsLoggedIn(false)} />;
  }

  return (
    <div className="app">
      <header>
        <button
          className="connexion-btn"
          onClick={() => {
            setShowLogin(true);
            setShowRegister(false);
          }}
        >
          Connexion
        </button>
      </header>

      {!showLogin && !showRegister && (
        <main>
          <h1>Bienvenue sur le Créateur de CV</h1>
          <p>Générez votre CV facilement ou choisissez un modèle existant.</p>

          <button className="create-btn">Créer un CV</button>

          <h2>Utiliser un modèle</h2>
          <div className="models">
            <div className="model">Modèle 1</div>
            <div className="model">Modèle 2</div>
            <div className="model">Modèle 3</div>
          </div>
        </main>
      )}

      {showLogin && (
        <LoginForm
          onClose={() => setShowLogin(false)}
          onSwitch={() => {
            setShowLogin(false);
            setShowRegister(true);
          }}
          onLogin={handleLogin}
        />
      )}

      {showRegister && (
        <RegisterForm
          onClose={() => setShowRegister(false)}
          onSwitch={() => {
            setShowRegister(false);
            setShowLogin(true);
          }}
        />
      )}
    </div>
  );
}

export default App;
