import React, { useState } from "react";
import "./CreateCV.css";

function CreateCV({ onBack }) {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    education: "",
    experience: "",
    skills: "",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("CV créé :", formData);
    alert("CV créé avec succès !");
    onBack();
  };

  return (
    <div className="create-cv">
      <div className="create-cv-header">
        <button className="back-btn" onClick={onBack}>
          ← Retour
        </button>
        <h2>Créer un CV</h2>
      </div>

      <form className="cv-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nom complet</label>
          <input
            type="text"
            name="name"
            placeholder="Ex: Jean Dupont"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            placeholder="Ex: jean.dupont@gmail.com"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Téléphone</label>
          <input
            type="text"
            name="phone"
            placeholder="Ex: +33 6 12 34 56 78"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Formation</label>
          <textarea
            name="education"
            placeholder="Ex: Diplôme, université, année..."
            value={formData.education}
            onChange={handleChange}
            rows="3"
          ></textarea>
        </div>

        <div className="form-group">
          <label>Expérience</label>
          <textarea
            name="experience"
            placeholder="Ex: Poste, entreprise, durée..."
            value={formData.experience}
            onChange={handleChange}
            rows="3"
          ></textarea>
        </div>

        <div className="form-group">
          <label>Compétences</label>
          <textarea
            name="skills"
            placeholder="Ex: React, Node.js, Java, SQL..."
            value={formData.skills}
            onChange={handleChange}
            rows="2"
          ></textarea>
        </div>

        <button type="submit" className="save-btn">Enregistrer le CV</button>
      </form>
    </div>
  );
}

export default CreateCV;
