import React, { useState } from "react";
import "./RegisterForm.css";

function RegisterForm({ onClose, onSwitch, onRegister }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name && email && password) {
      // Ici tu peux ajouter ton appel API pour cr√©er un compte
      onRegister(name, email, password);
      alert(`Compte cr√©√© pour ${name}`);
      onClose();
    } else {
      alert("Veuillez remplir tous les champs !");
    }
  };

  return (
    <div className="overlay">
      <div className="form-container">
        <button className="close-btn" onClick={onClose}>
          ‚úñ
        </button>
        <h2 className="form-title">Cr√©er un compte</h2>

        <form onSubmit={handleSubmit} className="register-form">
          <div className="form-group">
            <label>Nom complet</label>
            <input
              type="text"
              placeholder="Nom complet"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              placeholder="exemple@gmail.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="form-group password-input">
            <label>Mot de passe</label>
            <div className="password-wrapper">
              <input
                type={showPassword ? "text" : "password"}
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <span
                className="toggle-password"
                onClick={() => setShowPassword(!showPassword)}
                title={showPassword ? "Masquer" : "Afficher"}
              >
                {showPassword ? "üôà" : "üëÅÔ∏è"}
              </span>
            </div>
          </div>

          <button type="submit" className="register-btn">
            S‚Äôinscrire
          </button>
        </form>

        <p className="switch-text">
          D√©j√† un compte ?{" "}
          <span className="link" onClick={onSwitch}>
            Se connecter
          </span>
        </p>
      </div>
    </div>
  );
}

export default RegisterForm;
